import React, { useContext, useEffect, useState } from "react";
import {navigate} from "@reach/router";
import { UserContext } from "../UserContext";


const CreateCompany = () => {
    const [name, setName] = useState("");
    const [establishmentYear, setEstablishmentYear] = useState("");
    const [country, seCountry] = useState("");
    const [description, setDescription] = useState("");
    const [logo, setLogo] = useState("");
    const {user, setUser} = useContext(UserContext);


    function onChangeDescription(e) {
        setDescription(e.target.value);
    }

    function onChangeLogo(e) {
        setLogo(e.target.value);
    }

    function onChangeCountry(e) {
        seCountry(e.target.value);
    }

    function onEstablishmentYear(e) {
        setEstablishmentYear(e.target.value);
    }

    function onChangeName(e) {
        setName(e.target.value);
    }
    
    const handleAdd = (e) => {
        e.preventDefault();

        const options = {headers:{
            Authorization: "Bearer " + localStorage.getItem("token")
        }};
        
        fetch("http://localhost:5000/api/company", {
            method: "POST",
            headers: {
                "Authorization": "Bearer " + localStorage.getItem("token"),
                "Content-type": "application/json;charset=UTF-8"},
            body: JSON.stringify({
                name: name,
                establishmentYear: establishmentYear,
                country: country,
                description: description,
                logo: logo
            }),
            
        })
        .then((resp) => resp.json())
        .then((data) => {
            navigate('/'); 
        })
        .catch((err) => console.log(err));
    }

    return(
        <div>
            <h2>Create Company</h2>
            <form onSubmit={(e) => handleAdd(e)}>

            <label htmlFor="name">Name</label>
            <input
                type="text"
                value={name}
                onChange={onChangeName}
                onBlur={onChangeName}
            ></input>
            <br/>

            <label htmlFor="establishmentYear">Establishment Year</label>
            <input
                type="text"
                value={establishmentYear}
                onChange={onEstablishmentYear}
                onBlur={onEstablishmentYear}
            ></input><br/>

            <label htmlFor="country">Country</label>
            <input
                type="text"
                value={country}
                onChange={onChangeCountry}
                onBlur={onChangeCountry}
            ></input>
            <br/>

            <label htmlFor="logo">Link for logo</label>
            <input
                type="text"
                value={logo}
                onChange={onChangeLogo}
                onBlur={onChangeLogo}
            ></input><br/>

            <label htmlFor="description">Description</label>
            <input
                type="text"
                value={description}
                onChange={onChangeDescription}
                onBlur={onChangeDescription}
            ></input>
            <br/><br/>

            <button type="submit">Insert</button>
            </form>
            <br/>
            <button onClick={() => navigate('/')}>Home</button>
        </div>
    );
}

export default CreateCompany;
